import { css } from '@emotion/core';

export const textaAreaStyle = css`
  position: absolute;
  z-index: 100;
  border-style: solid;
  border-width: 1.5px;
  border-color: #5ac2ff;
  margin: 0px;
  overflow: hidden;
  background: none;
  outline: none;
  resize: none;
`;
