/** @jsx jsx */

import React, { Component, createRef, RefObject } from 'react';
import { jsx } from '@emotion/core';
import { Text, Path, Group } from 'react-konva';

export interface ICharacteristicElement {
  priority: string;
  icon: string;
  text: string;
}

interface ICharacteristicsProps {
  canvasWidth: number;
  canvasHeight: number;
  characteristics: {
    [priority: string] : ICharacteristicElement
  };
}

class CharacteristicsLayer extends Component<ICharacteristicsProps, any> {

  setPosition() {

  }

  render() {
    const {canvasHeight, canvasWidth, characteristics} = this.props;
    console.log(characteristics);
    const keys = Object.keys(characteristics);
    console.log(characteristics[keys[0]]);
    return (
      <Group
      x={canvasWidth*0.1}
      y={canvasHeight*0.7}
      height={canvasHeight*0.2}
      width={canvasWidth*0.5}
      visible={true}>
        {Object.keys(characteristics).sort().map(index => (
            <Group x={100}>
              <Path
              data={characteristics[index].icon}
              fill={'red'}
              scaleX={0.1}
              scaleY={0.1} />
              <Text
              text={characteristics[index].text}
              fontSize={16}/>
            </Group>
        ))}
      </Group>
    )
  }

}

export default CharacteristicsLayer;
